    @Test
    void contextLoads() {
    }

    @Test
    public void givenBidirectionRelation_whenUsingJacksonReferenceAnnotationWithSerialization_thenCorrect()
            throws JsonProcessingException {
        final User user = new User(1, "John", null);
        final Item item1 = new Item(2, "item1", user);
        final Item item2 = new Item(4, "item2", user);
        final String userJson = new ObjectMapper().writeValueAsString(user);
        final String item1Json = new ObjectMapper().writeValueAsString(item1);
        final String item2Json = new ObjectMapper().writeValueAsString(item2);

        log.info("userJson" + userJson);
        log.info("adding items to user");
        log.info("item1Json" + item1Json);
        log.info("itemJson" + item2Json);

        user.addItem(item1);
        user.addItem(item2);
        final String userJsonAfter = new ObjectMapper().writeValueAsString(user);
        log.info("--->userJson" + userJsonAfter);

        assertTrue(item1Json.contains("item1"));
        assertFalse(item1Json.contains("John"));
        assertFalse(item1Json.contains("owner"));

        assertTrue(userJson.contains("John"));
        assertThat(userJson.contains("userItems"));
        assertThat(userJson.contains("item1"));

    }
